import React, { useState, useEffect } from 'react';
import { useParams } from "react-router-dom";
import Card from "react-bootstrap/Card";
import Container from "react-bootstrap/Container";

export default function Detail() {
  const [mascota, setMascota] = useState(null);
  const { mascotaId } = useParams();

  useEffect(() => {
    const URL = "https://gist.githubusercontent.com/josejbocanegra/829a853c6c68880477697acd0490cecc/raw/99c31372b4d419a855e53f0e891246f313a71b20/mascotas.json";
    fetch(URL)
      .then(response => response.json())
      .then(data => {
        const foundMascota = data.find(pet => pet.id === parseInt(mascotaId));
        setMascota(foundMascota);
      })
      .catch(error => console.error('Error fetching pet data:', error));
  }, [mascotaId]);

  if (!mascota) {
    return <div>Loading...</div>;
  }

  return (
    <Container className="mt-3">
      <Card className="mx-auto" style={{ maxWidth: '600px' }}>
        <Card.Header className="bg-dark text-white">
          <h5 className="mb-0">Ad√≥ptame</h5>
        </Card.Header>
        <Card.Body className="p-0">
          <Card.Title className="text-center my-3">{mascota.nombre}</Card.Title>
          <Card.Img variant="top" src={mascota.foto} alt={mascota.descripcion} />
          <div className="p-3">
            <Card.Text>{mascota.descripcion}</Card.Text>
            <Card.Text>
              <strong>Raza:</strong> {mascota.raza}
            </Card.Text>
          </div>
        </Card.Body>
      </Card>
    </Container>
  );
}
